import React, { useState } from 'react';
import Menu from './Menu';
import Cart from './Cart';
import Contact from './Contact';

function App() {
  const [screen, setScreen] = useState('home');
  const [cartItems, setCartItems] = useState([]);

  const navItems = [
    { label: 'Home', value: 'home' },
    { label: 'Menu', value: 'menu' },
    { label: 'Cart', value: 'cart' },
    { label: 'Contact', value: 'contact' },
  ];

  // Cart management functions
  const addToCart = (item) => {
    setCartItems(prevItems => [...prevItems, item]);
  };

  const removeFromCart = (itemId) => {
    setCartItems(prevItems => {
      const itemIndex = prevItems.findIndex(item => item.id === itemId);
      if (itemIndex !== -1) {
        const newItems = [...prevItems];
        newItems.splice(itemIndex, 1);
        return newItems;
      }
      return prevItems;
    });
  };

  const clearCart = () => {
    setCartItems([]);
  };

  const renderScreen = () => {
    switch (screen) {
      case 'menu':
        return <Menu cartItems={cartItems} onAddToCart={addToCart} />;
      case 'cart':
        return (
          <Cart 
            cartItems={cartItems} 
            onUpdateCart={addToCart}
            onRemoveFromCart={removeFromCart}
            onClearCart={clearCart}
          />
        );
      case 'contact':
        return <Contact />;
      case 'home':
      default:
        return (
          <div>
            <h2>Welcome to Foodies!</h2>
            <p>Your favorite food ordering app.</p>
            <button onClick={() => setScreen('menu')}>Start Ordering</button>
          </div>
        );
    }
  };

  return (
    <div>
      <header className="app-header">
        <div className="header-container">
          <div className="logo">
            Foodies
          </div>
          <nav>
            <ul className="nav-list">
              {navItems.map((item) => (
                <li
                  key={item.value}
                  className={`nav-item ${screen === item.value ? 'active' : ''}`}
                  onClick={() => setScreen(item.value)}
                >
                  {item.label}
                </li>
              ))}
            </ul>
          </nav>
        </div>
      </header>
      <main style={{ padding: '32px' }}>{renderScreen()}</main>
    </div>
  );
}

export default App;
