import React, { useState } from 'react';
import Menu from './Menu';
import Cart from './Cart';
import Contact from './Contact';

function App() {
  const [screen, setScreen] = useState('home');
  const [cartItems, setCartItems] = useState([]);

  const navItems = [
    { label: 'Home', value: 'home' },
    { label: 'Menu', value: 'menu' },
    { label: 'Cart', value: 'cart' },
    { label: 'Contact', value: 'contact' },
  ];

  // Cart management functions
  const addToCart = (item) => {
    setCartItems(prevItems => [...prevItems, item]);
  };

  const removeFromCart = (itemId) => {
    setCartItems(prevItems => {
      const itemIndex = prevItems.findIndex(item => item.id === itemId);
      if (itemIndex !== -1) {
        const newItems = [...prevItems];
        newItems.splice(itemIndex, 1);
        return newItems;
      }
      return prevItems;
    });
  };

  const clearCart = () => {
    setCartItems([]);
  };

  const renderScreen = () => {
    switch (screen) {
      case 'menu':
        return <Menu cartItems={cartItems} onAddToCart={addToCart} />;
      case 'cart':
        return (
          <Cart 
            cartItems={cartItems} 
            onUpdateCart={addToCart}
            onRemoveFromCart={removeFromCart}
            onClearCart={clearCart}
          />
        );
      case 'contact':
        return <Contact />;
      case 'home':
      default:
        return (
          <div>
            <h2>Welcome to Foodies!</h2>
            <p>Your favorite food ordering app.</p>
            <button onClick={() => setScreen('menu')}>Start Ordering</button>
          </div>
        );
    }
  };

  return (
    <div>
      <header
        style={{
          borderBottom: '1px solid #eee',
          padding: '0',
        }}
      >
        <div
          style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            maxWidth: 900,
            margin: '24px auto',
            padding: '16px 0',
            border: '2px solid #ff6b6b',
            borderRadius: '12px',
            background: 'none',
          }}
        >
          <div style={{ fontWeight: 'bold', fontSize: '1.5rem', color: '#ff6b6b', marginLeft: 32 }}>
            Foodies
          </div>
          <nav>
            <ul
              style={{
                display: 'flex',
                gap: '24px',
                listStyle: 'none',
                margin: 0,
                padding: 0,
                marginRight: 32,
                background: 'none',
              }}
            >
              {navItems.map((item) => (
                <li
                  key={item.value}
                  style={{
                    cursor: 'pointer',
                    color: '#333',
                    fontWeight: 'normal',
                    background: 'none',
                  }}
                  onClick={() => setScreen(item.value)}
                >
                  {item.label}
                </li>
              ))}
            </ul>
          </nav>
        </div>
      </header>
      <main style={{ padding: '32px' }}>{renderScreen()}</main>
    </div>
  );
}

export default App;
